import Layout from "../components/layout";
import { withRouter } from "next/router";
//import Fetch  from 'isomorphic-fetch';

import ItemsList from "../components/itemsList";
import Breadcrumbs from "../components/breadcrumbs";


interface categorias {
  id: string;
  name: string;
  results: number;
}
interface item {
  id: string;
  title: string;
  price: {
    currency:string;
    amount:number;
    decimals:number;
  },
  picture:string;
  condition:string;
  free_shipping:boolean;
  city: string;
}




function Items(props) {
  console.log(props);
  return(
      <>
      <Layout>
        <Breadcrumbs
        categories={props.res.categories}
        ></Breadcrumbs>
         <ItemsList listItems={props.res}>

         </ItemsList>
      </Layout>
      </>
  )
}


Items.getInitialProps = async (ctx)=> {
 

  const res = await fetch("https://api.mercadolibre.com/sites/MLA/search?q=" + ctx.query.search);
  const json = await res.json();
 
  var resp ={
    author:
    {
      name:'Luis',
      lastname:'mendez'
    },
    categories:getCategories(json.available_filters[0].values),
    items:getItems(json.results)
  }
  return { res: resp };
}
function getCategories(listCategorias:categorias[]) {
  listCategorias.sort((a: categorias,b: categorias)=>{
    return b.results - a.results ;
  });
  var listado:string[] =[];
  listCategorias.forEach((element: categorias) => {
    listado.push(element.name);
  });
    return listado; 
}
function getItems(results:any[])
{
  var items: item[] =[];
  var limit =4;
  if(results.length<4)
    limit = results.length;
    
  for(var i = 0; i<limit ; i++)
  {    
    items.push({
      id:results[i].id,
      title: results[i].title,
      picture:results[i].thumbnail,
      condition:results[i].condition,
      city:results[i].address.state_name,
      free_shipping:results[i].shipping.free_shipping,
      price:{
        currency:results[i].currency_id,
        amount:results[i].price,
        decimals:0
      }
    });
  }
  return items;

}
export default Items;
