import React, { Component } from "react";
import { Col, Row, InputGroup, FormControl, Button } from "react-bootstrap";
import imageLogo from "../assets/Logo_ML.png";
import "../styles/sass/basic.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { withRouter   } from 'next/router';

type estado = {
  value: string;
};


interface IHeaderState {
  query?: string;
}

function RedirectPage({ ctx }) {
  const router = useRouter()
  // Make sure we're in the browser
  if (typeof window !== 'undefined') {
    router.push('/new/url');
    return; 
  }
}

class Header extends Component{
  
  constructor(props) {
    super(props);
       
  }
  handleChange= e =>{
    this.setState({
      [e.target.name]:e.target.value

    });
   
  }
  
  handleSubmit = e =>{
    
    e.preventDefault();    
    console.log(this.state);
    this.props.router.push('/items?search=' + this.state.query);
   
  }
  render() {
    const { title } = this.props;
    return (
      <Row className="header">
        <Col>
          <img src={imageLogo} alt="Logo Meli" className="float-right" />
        </Col>
        <Col xs={6}>
          <form onSubmit={this.handleSubmit}> 
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Buscar Productos, marcas y mas..."
                aria-label="Buscador"
                aria-describedby="basic-addon2"
                name="query"
                onChange={this.handleChange}
              />
              <InputGroup.Append>
                <Button
                 
                  className="buttonSearch"
                  variant="outline light"
                  type="submit"
                >
                  <FontAwesomeIcon icon={faSearch} />
                </Button>
              </InputGroup.Append>
            </InputGroup>
          </form>
        </Col>
        <Col></Col>
      </Row>
    );
  }
}
export default Header;
