import fetch  from 'isomorphic-fetch';
// import helpers from '../../helpers/searchHelpers';

export default async (req, res) => {
    try {
        console.log(req)
        const res1 = await fetch("https://api.mercadolibre.com/sites/MLA/search?q=" + req.query.id);
        const json = await res1.json();
        // var helper = new helpers().buildResponse(json);
        res.status(200).json({respuesta:"hola"});
    } catch (error) {
        res.status(500);
    }
}
