class DisplayHelpers {
    constructor() {}
    displayCurrency=(currencyCode:string):string=>{
        return currencyCode?"$" : "USD $";
    }
    formatPrice=(price:number) : string=>{
         'de-DE' 
        return price.NumberFormat('de-DE');

    }
}
export default DisplayHelpers;
