import fetch  from 'isomorphic-fetch';
 import helpers from '../../helpers/detailHelpers';

export default async (req, res) => {
    try {
        console.log(req)
         
    const res1 = await fetch("https://api.mercadolibre.com/items/" + req.query.id);
    const json1 = await res1.json();   

    const res2 = await fetch("https://api.mercadolibre.com/items/" + req.query.id+"/description");
    const json2 = await res2.json();   
    var helper = new helpers().buildResponse({detalle:json1, description:json2});
        res.status(200).json(helper);
    } catch (error) {
        res.status(500);
    }
}
