import React, { Component } from "react";
import { Col, Row, InputGroup, FormControl, Button } from "react-bootstrap";
import "../styles/sass/itemCard.scss";
class ItemCard extends Component {
  constructor(props) {
    super(props);
    console.log(props);
  }
   formatPrice:string = price =>{
    return price.amount;
  }
  render() {
    return (
      <div>
        <Row className="itemCard">
          <Col className="lateral-derecho">
            <img
              src={this.props.dataItem.picture}
              alt={this.props.dataItem.title}
              className="imagenCard"
            />
          </Col>
          <Col>
          <div className="price-tag">
              <span >{this.formatPrice(this.props.dataItem.price)} </span>
          </div>
          <div className="title-tag">
              <span >{this.props.dataItem.title} </span>
          </div>
            
          </Col>
          <Col></Col>
        </Row>
      </div>
    );
  }
}
export default ItemCard;
