import { Col, Row,InputGroup, FormControl, Button } from "react-bootstrap";
import imageLogo from '../assets/Logo_ML.png';
import "../styles/sass/basic.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'


function handleClick(e:any) {
  e.preventDefault();
  console.log('The link was clicked.');
}

class Header extends React.Component {
  constructor(props:any) {
    super(props);
     var state : {value:string};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event:any) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event:any) {
    alert('A name was submitted: ' + state.value);
    event.preventDefault();
  }

  render() {
    return (
      <Row className="header">
        <Col >
        <img src={imageLogo} alt='Logo Meli' className="float-right"/>
        
        </Col>
        <Col xs={6}>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Buscar Productos, marcas y mas..."
              aria-label="Recipient's username"
              aria-describedby="basic-addon2"
              value={this.state.value} onChange={this.handleChange}
            />
            <InputGroup.Append>
            
            <Button onClick={handleClick} className="buttonSearch"  variant="outline light">            
              <FontAwesomeIcon icon={faSearch} />
            </Button>
            </InputGroup.Append>
          </InputGroup>
        </Col>
        <Col></Col>
      </Row>
    );

  }
  
};
export default Header;
