import React, { Component } from "react";
import { Col, Row, InputGroup, FormControl, Button } from "react-bootstrap";
import "../styles/sass/itemCard.scss";
class ItemCard extends Component {
  constructor(props) {
    super(props);
    console.log(props);
  }
  render() {
    return (
      <div>
        <div className="itemCard">
          <div className="lateral-derecho">
          <img
            src={this.props.dataItem.picture}
            alt={this.props.dataItem.title}
            className="imagenCard"
          />

            
          </div>
          <div className="centro">
            <h3>{this.props.dataItem.price.amount}</h3>
          </div>
          <div className="lateral-izquiero">
          </div>
        </div>
        
      </div>
    );
  }
}
export default ItemCard;
