import React from 'react'
import { Col, Row,InputGroup, FormControl, Button } from "react-bootstrap";
import imageLogo from '../assets/Logo_ML.png';
import "../styles/sass/basic.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'


function handleClick(e:any) {
  e.preventDefault();
  console.log('The link was clicked.');
}
type estado= {
  value:string;
}

interface IProps {
}

interface IState {
  query?: string;
}
class Header extends React.Component<IProps, IState> { 
  constructor(props: IProps) {
   state.query="";
    super(props);
   

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  

  handleSubmit(event:any) {
    alert('A name was submitted: ' + this.state.query);
    event.preventDefault();
  }

  render() {
    return (
      <Row className="header">
        <Col >
        <img src={imageLogo} alt='Logo Meli' className="float-right"/>
        
        </Col>
        <Col xs={6}>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Buscar Productos, marcas y mas..."
              aria-label="Buscador"
              aria-describedby="basic-addon2"
              value={this.state.query} 
            />
            <InputGroup.Append>
            
            <Button onClick={handleClick} className="buttonSearch"  variant="outline light">            
              <FontAwesomeIcon icon={faSearch} />
            </Button>
            </InputGroup.Append>
          </InputGroup>
        </Col>
        <Col>
        {this.state.query} 
        </Col>
      </Row>
    );

  }
  
};
export default Header;
