import React, { Component } from 'react';
import { Col, Row,InputGroup, FormControl, Button } from "react-bootstrap";
import imageLogo from '../assets/Logo_ML.png';
import "../styles/sass/basic.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'


function handleClick(e:any) {
  e.preventDefault();
  console.log('The link was clicked.');
}
type estado= {
  value:string;
}

interface IHeaderProps {
  title?: string
}

interface IHeaderState {
  query?: string
 };
 
class Header extends Component<IHeaderProps, IHeaderState> { 
  
  constructor(props:IHeaderProps){

    super(props);
    this.state ={
      query : ""

    } ;
  }
 
  

  render() {
    const {title} = this.props;
    return (
      <Row className="header">
        <Col>
          <img src={imageLogo} alt="Logo Meli" className="float-right" />
        </Col>
        <Col xs={6}>
          <form>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Buscar Productos, marcas y mas..."
              aria-label="Buscador"
              aria-describedby="basic-addon2"
            />
            <InputGroup.Append>
              <Button
                onClick={handleClick}
                className="buttonSearch"
                variant="outline light"
              >
                <FontAwesomeIcon icon={faSearch} />
              </Button>
            </InputGroup.Append>
          </InputGroup>

          </form>
         
        </Col>
        <Col>{this.state.query}</Col>
      </Row>
    );

  }
  
};
export default Header;
