import "../models/categorias";
import "../models/item";
import "./authorHelper";
import getAuthor from "./authorHelper";

class SearchHelpers {
  constructor() {}
  getCategories = (listCategorias: categorias[]): string[] => {
    listCategorias.sort((a: categorias, b: categorias) => {
      return b.results - a.results;
    });
    var limit = 4;
    var listado: string[] = [];
    for (var i = 0; i < limit; i++) {
      listado.push(listCategorias[i].name);
    }
    return listado;
  };
  buildResponse = (resMELI: any): any => {
    var resp = {
      author: getAuthor(),
      categories: this.getCategories(resMELI.available_filters[0].values),
      items: this.getItems(resMELI.results),
    };
    return resp;
  };

  getItems = (results: any[]): item[] => {
    var items: item[] = [];
    var limit = 4;
    if (results.length < 4) limit = results.length;

    for (var i = 0; i < limit; i++) {
      items.push({
        id: results[i].id,
        title: results[i].title,
        picture: results[i].thumbnail,
        condition: results[i].condition,
        city: results[i].address.state_name,
        free_shipping: results[i].shipping.free_shipping,
        price: {
          currency: results[i].currency_id,
          amount: results[i].price,
          decimals: 0,
        },
      });
    }
    return items;
  };
}
export default SearchHelpers;
