import React, { Component } from 'react';
import { Col, Row,InputGroup, FormControl, Button } from "react-bootstrap";
import imageLogo from '../assets/Logo_ML.png';
import "../styles/sass/basic.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'


function handleClick(e:any) {
  e.preventDefault();
  console.log('The link was clicked.');
}
type estado= {
  value:string;
}

interface IAppProps {
  title?: string
}

interface IAppState { };
class Header extends Component<IAppProps, IAppState> { 
  
  
  static defaultProps: IAppProps = {
    title: 'REACT WITH TYPESCRIPT'
  }

  render() {
    return (
      <Row className="header">
        <Col >
        <img src={imageLogo} alt='Logo Meli' className="float-right"/>
        
        </Col>
        <Col xs={6}>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="Buscar Productos, marcas y mas..."
              aria-label="Buscador"
              aria-describedby="basic-addon2"
              value={this.props.title} 
            />
            <InputGroup.Append>
            
            <Button onClick={handleClick} className="buttonSearch"  variant="outline light">            
              <FontAwesomeIcon icon={faSearch} />
            </Button>
            </InputGroup.Append>
          </InputGroup>
        </Col>
        <Col>
        {this.state.query} 
        </Col>
      </Row>
    );

  }
  
};
export default Header;
